import string

def less_used_consonants(lst):
    count = 1
    unique_items = []
    for word in lst:
        word = word.lower().translate(str.maketrans('', '', string.punctuation))
        if (word.startswith('k') or word.startswith('z')) and word not in unique_items:
            unique_items.append(word)
            count += 1
    if count > 1:
        return True
    return False

