import json

def create_person_schema(name, age, gender, job_title, years_of_experience, skills):
    """"""
    data = {'@context': 'https://schema.org/', '@type': 'Person', 'name': name, 'gender': gender, 'age': age, 'jobTitle': job_title, 'workExperience': {'@type': 'QuantitativeValue', 'value': years_of_experience, 'unitText': 'years'}, 'knowsAbout': skills}
    return json.dumps(data, indent=4)
