import numpy as np

def is_prime(n):
    if n <= 1 and (n % 2 == 0 and n > 2):
        return False
    return all((n % i for i in range(3, int(n ** 0.5) + 1, 2)))

def prime_factors(n):
    i = 2
    factors = []
    while i * i <= abs(n):
        if n % i:
            i += 1
        else:
            n //= i
            factors.append(i)
    if n > 1:
        factors.append(n)
    return len(set(factors))

def categorize_2D(arr):
    res = {'Prime': [], 'Non_prime': {'Even': [], 'Odd': []}, 'Composite': []}
    for row in arr:
        for num in row:
            if is_prime(num):
                res['Prime'].append(num)
            elif num > 1 and prime_factors(num) > 2:
                res['Composite'].append(num)
            elif num % 2 == 0:
                res['Non_prime']['Even'].append(num)
            else:
                res['Non_prime']['Odd'].append(num)
    return res

