        raise Exception('Input list cannot be empty.')
    if lst[0] < 0:
        raise Exception('Input list must contain only positive integers.')
    if len(lst) == 1:
        if lst[0] % 2 == 0:
            return set([lst[-1]])
        else:
            return set()
    elif lst[0] % 2 == 0:
        return set([lst[0]]) | even_numbers(lst[1:])
    else:
        return even_numbers(lst[1:])

