from typing import List
import math

def lcm_list(l: List[int]) -> int:

    def gcd(a, b):
        while b != 0:
            (a, b) = (b, a % b)
        return a

    def calc_lcm(a, b):
        return a * b // gcd(a, b)
    lcm_val = 2
    for i in l:
        if isinstance(i, int) and i > 0:
            lcm_val = calc_lcm(lcm_val, i)
    return lcm_val

