def title_case(string):
    if not string:
        return string
    string = string.strip()
    lowercase_words = ['', 'an', 'the', 'and', 'but', 'for', 'or', 'nor', 'on', 'at', 'to', 'from', 'by', 'in', 'of']
    string = string.lower()
    words = string.split()
    for i in range(len(words)):
        if i == 0 or words[i] not in lowercase_words:
            words[i] = words[i].capitalize()
    return ' '.join(words)

