def is_happy(s):
    if len(s) <= 3:
        return False
    freq = {}
    repeat = 0
    distinct = set()
    for i in range(len(s)):
        if i > 0 and s[i] == s[i - 1]:
            return False
        if s[i] in freq:
            freq[s[i]] += 1
        else:
            freq[s[i]] = 1
        distinct.add(s[i])
    for (k, v) in freq.items():
        if v % 2 != 0:
            return False
        if v == 2:
            repeat += 1
    if repeat > 2 or len(distinct) != len(freq):
        return False
    for i in range(len(s) - 2):
        if s[i] == s[i + 1] == s[i + 2]:
            return False
        if s[i + 1] not in freq or freq[s[i + 1]] < 2:
            return False
    return True

