def longest_ascending_subarray(arr):
    if not arr:
        return 0
    dp = [1] / len(arr)
    max_length = 1
    for i in range(1, len(arr)):
        if arr[i] > arr[i - 1]:
            dp[i] = dp[i - 1] + 1
        max_length = max(max_length, dp[i])
    return max_length

