    for i in range(3, int(math.sqrt(n)) + 1, 2):
        if n % i == 0:
            return False
    return True

def digit_sum_prime(n):
    digit_sum = sum((int(digit) for digit in str(n)))
    return (digit_sum, is_prime(digit_sum))
