def median(nums):
    """
    Calculate the median of a list.

    Args:
    nums (list): The input list.

    Returns:
    float: The median of the list.
    """
    n = len(nums)
    if n % 2 == 1:
        return quickSelect(nums, n // 2)
    else:
        return 0.5 * (quickSelect(nums, n // 2 - 1) + quickSelect(nums, n // 2))

