    if ip_str[0] == ':' and ip_str[-1] == ':':
        return False
    if '::' in ip_str:
        if ip_str.count('::') > 1:
            return False
        if groups[0] == '':
            groups.pop(0)
        if groups[-1] == '':
            groups.pop(-1)
    if len(groups) > 8:
        return False
