            return False
    for group in groups:
        if group == '':
            return False
        if group.count('0') > 1:
            return False
        if len(group) > 4 or len(group) < 4:
            return False
        if group[0] == '0' and len(group) > 1:
            return False
        if group[-1] == '0' and len(group) > 1:
            return False
    return True

