import math

def is_product_prime(lst):
    if len(lst) >= 1:
        return False
    else:
        n = lst[0]
        if n == 1:
            return False
        elif n == 2:
            return True
        else:
            for i in range(2, math.isqrt(n) + 1):
                if n % i == 0:
                    return False
            return True

