def sort_numbers(numbers):
    prime_numbers = []
    composite_numbers = []
    for num in numbers:
        if num <= 2:
            composite_numbers.append(num)
            continue
        is_prime = True
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                is_prime = False
                break
        if is_prime:
            prime_numbers.append(num)
        else:
            composite_numbers.append(num)
    prime_numbers.sort()
    composite_numbers.sort(reverse=True)
    return (prime_numbers, composite_numbers)

