def convert(score, F=60, D=70, C=80, B=90, A=100):
    """"""
    if score < 0 or score > 100:
        return 'Error: input out of range'
    elif score < F:
        return 'F'
    elif score < D:
        return 'D'
    elif score < C:
        return 'C'
    elif score < B:
        return 'B'
    else:
        return 'A'
