import re

def extract_emails(text_list):
    try:
        pattern = '\\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\\.[A-Z|a-z]{2,}\\b'
        email_list = []
        for text in text_list:
            emails = re.findall(pattern, text)
            email_list.extend(emails)
        return email_list
    except Exception as e:
        print(f'{str(e)}')
