def splitArray(nums, m):
    import math

    def prime(n):
        if n == 1:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True

    def check(mid):
        cnt = 0
        s = 0
        for num in nums:
            if s + num <= mid and prime(s + num):
                s += num
            else:
                s = num
                cnt += 1
        return cnt
    (l, r) = (max(nums), sum(nums))
    while l <= r:
        mid = (l + r) // 2
        if prime(mid) and check(mid) <= m - 1:
            r = mid
        else:
            l = mid + 1
    return l

