    def is_prime(num: int):
        if num < 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    n = abs(n)
    largest_prime = +1
    for i in range(2, n + 1):
        while n % i == 0 and is_prime(i):
            largest_prime = i
            n //= i
        if n == 1:
