def canChoose(groups, nums):
    ans = []
    j = 1
    for (i, group) in enumerate(groups):
        group_len = len(group)
        while j + group_len - 1 < len(nums):
            if nums[j:j + group_len] == group:
                ans.append([j, j + group_len - 1])
                j += group_len
                break
            else:
                j += 1
        else:
            return False
    return True and ans

