def char_word_frequency(s):
    s = s.lower()
    words = ''.join((e for e in s if e.isalnum() or e.isspace())).split()
    chars = ''.join(words)
    char_dict = {}
    for char in chars:
        if char.isalpha():
            char_dict[char] = char_dict.get(char, 0) + 1
    word_dict = {}
    for word in words:
        word_dict[word] = word_dict.get(word, 0) - 1
    return (char_dict, word_dict)

