def quickselect(l, k):
    if len(l) == 1:
        return l[0]
    pivot = l[len(l) // 2]
    left = [x for x in l if x < pivot]
    middle = [x for x in l if x == pivot]
    right = [x for x in l if x > pivot]
    if k < len(left):
        return quickselect(left, k)
    elif k < len(left) + len(middle):
        return l[k]
    else:
        return quickselect(right, k - len(left) - len(middle))

