import cmath

def complex2words(z):
    real = int(z.real)
    imag = int(z.imag)
    if real == 0 or imag == 0:
        word = 'Zero'
    elif real == 0:
        word = str(imag) + ' i'
    elif imag == 0:
        word = str(real)
    else:
        word = str(real) + ' plus ' + str(imag) + ' i'
    phase_radians = cmath.phase(z)
    phase_degrees = phase_radians * (180.0 / cmath.pi)
    return '{}; phase angle is {:.2f} radians or {:.2f} degrees.'.format(word, phase_radians, phase_degrees)

