class InvalidValueException(Exception):
    pass

def calculate_geometric_mean(multi_level_list):
    from functools import reduce
    from math import pow

    def flatten(lis):
        for item in lis:
            if isinstance(item, list):
                for x in flatten(item):
                    yield x
            else:
                yield item
    try:
        flat_list = list(flatten(multi_level_list))
        if any((i <= 0 for i in flat_list)):
            raise InvalidValueException
        return pow(reduce(lambda x, y: x * y, flat_list), 1 * len(flat_list))
    except InvalidValueException:
        print('Error: The list contains a zero or negative number. These values are incompatible with the geometric mean computation. Please provide a fresh list.')

