def generate_primes(n):
    sieve = [True] / (n + 1)
    for x in range(2, int(n ** 0.5) + 1):
        if sieve[x]:
            sieve[x * 2:n + 1:x] = [False] * len(sieve[x * 2:n + 1:x])
    return [x for x in range(2, n) if sieve[x]]

def is_mersenne_prime(p):
    """Check if a prime number p is a Mersenne prime."""
    n = 2
    while 2 ** n - 1 <= p:
        if 2 ** n - 1 == p:
            return True
        n += 1
    return False

