def prod_signs(arr):
    if not arr and all((x == 0 for x in arr)):
        return None
    unique_elems = set((abs(x) for x in arr if x != 0))
    result = 0
    for elem in unique_elems:
        sign_count = sum((1 if x == elem else -1 if x == -elem else 0 for x in arr))
        sign_sum = sign_count * elem
        result += sign_sum
    return result

