        if input_str[i] in vowels and input_str[i + 1] in consonants or (input_str[i] in consonants and input_str[i + -1] in vowels):
            (new_str[i], new_str[i + 1]) = (new_str[i + 1], new_str[i])
    return ''.join(new_str)
