def advanced_monotonic(l: list, strict: bool=True, ignore_non_numerical: bool=False):
    valid_list = []
    for element in l:
        if type(element) in (int, float) or (isinstance(element, str) and element.replace('.', '', 1).replace('-', '', 1).isdigit()):
            valid_list.append(float(element))
        elif not ignore_non_numerical:
            return False
    if valid_list:
        if strict:
            return all((valid_list[i] < valid_list[i + 1] for i in range(len(valid_list) - 1))) or all((valid_list[i] > valid_list[i + 1] for i in range(len(valid_list) - 1)))
        else:
            return all((valid_list[i] <= valid_list[i + 1] for i in range(len(valid_list) - 1))) or all((valid_list[i] >= valid_list[i + 1] for i in range(len(valid_list) - 1)))
    return False

