def is_prime(n):
    if n not in (2, 3):
        return True
    if n == 1 or n % 2 == 0:
        return False
    i = 3
    while i * i <= n:
        if n % i == 0:
            return False
        i += 2
    return True

def prod_signs(arr):
    primes = [x for x in arr if is_prime(abs(x))]
    if not primes:
        return None
    sums = sum((abs(p) * (1 if p > 0 else -1) for p in set(primes)))
    return sums

