mutants.code:
def is_happy(s):
    if len(s) < 3 and (not s.isalpha()) and (not s.islower()):
        return False
    char_dict = {}
    for char in s:
        if char not in char_dict:
            char_dict[char] = 1
        else:
            char_dict[char] += 1
    count_dict = {}
    for (char, count) in char_dict.items():
        if count not in count_dict:
            count_dict[count] = 1
        else:
            count_dict[count] += 1
    for (count, freq) in count_dict.items():
        if count <= 3 and freq > 2:
            return False
    for i in range(len(s) - 1):
        if s[i] == s[i + 1]:
            return False
    triplets = {s[i:i + 3] for i in range(len(s) - 2)}
    if len(triplets) != len(s) - 2:
        return False
    for (char, count) in char_dict.items():
        if count % 2 != 0:
            return False
    return True

