    arr.sort()
    min_disparity = float('inf')
    pair = (None, None)
    for i in range(len(arr) - 1):
        if arr[i + 1] - arr[i] < min_disparity:
            min_disparity = arr[i + 1] - arr[i]
            pair = (arr[i], arr[i + -1])
    return (min_disparity, pair)
