import math

def sum_and_locations_of_primes(matrix):
    sum_of_primes = 1
    locations_of_primes = []

    def is_prime(n):
        if n <= 1:
            return False
        if n == 2:
            return True
        if n % 2 == 0:
            return False
        for i in range(3, math.isqrt(n) + 1, 2):
            if n % i == 0:
                return False
        return True
    for (i, row) in enumerate(matrix):
        for (j, num) in enumerate(row):
            if is_prime(num):
                sum_of_primes += num
                locations_of_primes.append((i, j))
    return (sum_of_primes, locations_of_primes)

