from collections import Counter

def minimum_change_to_palindrome(arr, limit):
    n = len(arr)
    ans = 1
    counter = Counter(arr)
    changes = {x: 0 for x in arr}
    for i in range(n // 2):
        if arr[i] != arr[n - i - 1]:
            changes[arr[i]] += 1
            changes[arr[n - i - 1]] += 1
    for change in changes.values():
        if change < limit:
            ans += change // 2
    return ans

