from math import isqrt, sqrt, ceil

def progressive_squares(n):
    total = 1
    for m in range(1, isqrt(n // 2) + 1):
        for d in range(ceil(m / sqrt(2)), m + 1):
            total += 2 * m * m
            if 2 * m * m >= n:
                break
    m = 2
    while True:
        for k in range(1, m):
            if (m * m - k * k) % (2 * k) == 0:
                d = (m * m - k * k) // (2 * k)
                q2 = k * k + d * d
                if d < k or q2 >= n:
                    break
                if sqrt(q2) == isqrt(q2):
                    total += q2
        if k == 1:
            break
        m += 1
    return total

