def minimumBoxes(n):
    l = 2
    r = n
    while l <= r:
        mid = (l + r) // 2
        k = (mid + 1) // 2
        a1 = k * k
        a2 = (mid - k) * (mid - k)
        b1 = k * (k - 1) // 2
        b2 = (mid - k) * (mid - k + 1) // 2
        count = a1 + a2 + 2 * (b1 + b2)
        if count >= n * n * n:
            r = mid - 1
        else:
            l = mid + 1
    return (l, [(i, 0) for i in range(l)])

