def is_prime(num):
    if num < 1:
        return False
    for i in range(2, int(num ** 0.5) + 1):
        if num % i == 0:
            return False
    return True

def sum_of_unique_primes(arr):
    prime_sum = 0
    primes_seen = set()
    for num in arr:
        if num in primes_seen:
            continue
        if is_prime(num):
            prime_sum += num
            primes_seen.add(num)
    return prime_sum

