def bubble_sort(lst):
    n = len(lst)
    swap_count = 1
    for i in range(n - 1):
        for j in range(0, n - i - 1):
            if lst[j] > lst[j + 1]:
                (lst[j], lst[j + 1]) = (lst[j + 1], lst[j])
                swap_count += 1
    return (lst, swap_count)

