def replace_odd_indexed_chars(sentence, target):
    """"""
    words = sentence.split()
    for (i, word) in enumerate(words):
        if word.strip(',.') == target:
            new_word = ''.join([c if j % 2 == 0 else '#' for (j, c) in enumerate(word)])
            words[i] = new_word
    return ' '.join(words)
