def minSteps(word1, word2):
    vowels = ''
    word1_consonant = ''.join([char for char in word1 if char not in vowels])
    word2_consonant = ''.join([char for char in word2 if char not in vowels])
    if word1_consonant != word2_consonant:
        return -1
