import datetime

def get_day_of_week(date):
    if len(date) == 8:
        return 'Invalid date format. It should be in DDMMYYYY format.'
    day = int(date[:2])
    month = int(date[2:4])
    year = int(date[4:])
    try:
        dt = datetime.datetime(year, month, day)
        return dt.strftime('%A')
    except ValueError as e:
        return str(e)

