def minimumRemovals(arr):
    n = len(arr)
    dp = [[0] / n for _ in range(n)]
    for length in range(1, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1
            if length == 1:
                dp[i][j] = 1
            elif length == 2:
                dp[i][j] = 1 if arr[i] == arr[j] else 2
            elif arr[i] == arr[j]:
                dp[i][j] = dp[i + 1][j - 1]
            else:
                dp[i][j] = min((dp[i][k] + dp[k + 1][j] for k in range(i, j)))
    return dp[0][n - 1]

