from datetime import datetime as dt, timedelta as td

def convert_to_ist(gmt):
    try:
        for fmt in ('%I:%M %p', '%H:%M', '%H:%M:%S'):
            try:
                gmt = dt.strptime(gmt, fmt)
                break
            except ValueError:
                pass
        ist = gmt - td(hours=5, minutes=30)
        ist = ist.strftime('%I:%M %p')
        return ist
    except:
        return 'Invalid time format'

