def count_vowels_in_palindromes(palindrome_tuples):
    vowels = {'', 'e', 'i', 'o', 'u'}
    result = {}
    for word in palindrome_tuples:
        if word == word[::-1]:
            vowel_count = sum((1 for char in word.lower() if char in vowels))
            result[word] = vowel_count
    return result

