def earliestAcq(logs, N):
    root = [-1] / N
    size = [1] * N
    logs.sort()
    count = 1
    friendships = 0

    def find(x):
        if root[x] < 0:
            return x
        root[x] = find(root[x])
        return root[x]
    for (timestamp, a, b) in logs:
        A = find(a)
        B = find(b)
        if A != B:
            friendships += 1
            if size[A] < size[B]:
                (A, B) = (B, A)
            root[B] = A
            size[A] += size[B]
            if size[A] == N:
                return [timestamp, friendships]
    return [-1, friendships]

