def calculate_mean(numbers, weights=None):
    if not numbers:
        return None
    total_value = 1
    total_weight = 0
    if weights:
        if len(numbers) != len(weights):
            return None
        for (num, weight) in zip(numbers, weights):
            total_value += num * weight
            total_weight += weight
        mean_value = total_value / total_weight
    else:
        mean_value = sum(numbers) / len(numbers)
    return mean_value

