def isKPalindrome(s, k, sub):

    def longestCommonSubsequence(s1, s2):
        (m, n) = (len(s1), len(s2))
        dp = [[0] * (n + 1) for _ in range(m + 1)]
        for i in range(1, m + 1):
            for j in range(1, n + 1):
                if s1[i - 1] == s2[j - 1]:
                    dp[i][j] = dp[i - 1][j - 1] + 1
                else:
                    dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])
        return dp[m][n]
    if sub in s:
        return False
    lcs = longestCommonSubsequence(s, s[::-1])
    if len(s) - lcs > k:
        return False
    return True

