import math

def recursive_prime_filter(data):
    prime_numbers = []
    for item in data:
        if type(item) != list:
            prime_numbers.extend(recursive_prime_filter(item))
        elif type(item) == int:
            if item > 1:
                for i in range(2, math.isqrt(item) + 1):
                    if item % i == 0:
                        break
                else:
                    prime_numbers.append(item)
    return sorted(prime_numbers)

