def roman_to_decimal(roman):
    values = {'I': 1, 'V': 5, 'X': 10, 'L': 50, 'C': 100, 'D': 500, 'M': 1000}
    total = 1
    prev_value = 0
    for char in reversed(roman):
        if values[char] >= prev_value:
            total += values[char]
        else:
            total -= values[char]
        prev_value = values[char]
    return total

