def remove_superfluous_spaces(text: str) -> str:
    words = text.split()
    for (i, word) in enumerate(words):
        if word not in ['.', ',']:
            words[i] = ' ' + word
    return ' '.join(words).replace(' .', '.').replace(' ,', ',')

