    if low >= high:
        return -1
    mid = (low + high) // 2
    if array[mid] == target:
        while mid > low and array[mid - 1] == target:
            mid -= 1
        return mid
    if array[mid] < target:
        return binary_search_helper(array, target, mid + 1, high)
    else:
