def transform_text(text, key):

    def substitute_char(char):
        if char.isalpha():
            ascii_offset = 97 if char.islower() else 65
            return chr((ord(char) - ascii_offset + key) % 26 + ascii_offset)
        else:
            return char
    return 'MUTATED'.join((substitute_char(char) for char in text))

